import{_ as e,a as i}from"./Docker-a3b62ffe.js";import{_ as s}from"./JavaScript-d0a12f02.js";import{_ as t,a as n}from"./cssLogo-9f7e8903.js";import{_ as o}from"./sql-c404065f.js";import{_ as a}from"./ImageSwitch.vue_vue_type_style_index_0_lang-258c3af4.js";import{d as r,o as l,c as p,g as d,a as u}from"./index-41c9b853.js";import"./sfml-logo-10c3558b.js";import"./arrowleft-1bbbf34d.js";const c=""+new URL("Rlogo-f9a5e1d1.svg",import.meta.url).href,m=u('<div class="information"><h4>Qu&#39;est-ce que le projet Visualisation ?</h4><div class="textes"><p>Le projet Visualisation est un projet en cours de réalisation dans le cadre de mon alternance au sein de l&#39;entreprise Diag2Tec. Il vise à simplifire la création de plots à partir de données saisies par les utilisateurs.</p><p>Ce projet présente de nombreux avantages pour l&#39;entreprise Diag2Tec car il permet d&#39;accéléré grandement leurs processus de création de plot numérique nécessaire à la rédaction de comtpes rendus ou de revues scientifiques.</p><p>En effet, jusqu&#39;a présent, l&#39;entreprise Diag2Tec utilisait le langage de programmation R pour la réalisation de leurs analyses et la création de visuels, ce qui représentait un travail long et fastidieux et dont seul une petite partie des employés y était formé.</p></div><h4>Technologies utilisées :</h4><div class="techno"><img src="'+c+'"><img src="'+e+'"><img src="'+s+'"><img src="'+t+'"><img src="'+n+'"><img src="'+o+'"><img src="'+i+'"></div><h4>Contraintes techniques :</h4><div class="textes"><p>Ce projet présentes de nombreuses contraintes. Premièrement, les plots doivent être générés exclusivement à partir de script R.</p><p>Ensuite, l&#39;application doit pouvoir communiquer avec une base de données pour récupérer les informations nécessaire a la création de ses plots.</p><p>De plus, l&#39;utilisateur doit pouvoir facilement ajouté ou mettre à jour des jeux de données.</p><p>Enfin, l&#39;utilisateur doit pouvoir personnalisé le plot comme il le souhaite (changer le textes, les couleurs, le type de plot...) et exporter les résultats aux formats pdf ou png.</p></div><h4>Fonctionnalités réalisés :</h4><ul><li><p>Modélisation d&#39;un schéma de base de données évolutif</p></li><li><p>Réalisation d&#39;un système d&#39;importation de données à partir de fichier CSV (création de table dynamique en BDD) permettant d&#39;écraser les contenu existants si besoin.</p></li><li><p>Création d&#39;une API permettant d&#39;interagir avec la base de données</p></li><li><p>Réalisation d&#39;un premier module d&#39;analyse d&#39;expression de gènes</p></li><li><p>Création et intégration des scripts R permettant la génération de BarPlot et BoxPlot pour l&#39;analyse d&#39;expression de gènes</p></li><li><p>Création d&#39;un outils de personnalisation de plot interagissant entre client-serveur / NodeJs-Langage R</p></li></ul><h4>Compétences acquises :</h4><ul><li><p>Acquisition de connaissances en bio-informatique</p></li><li><p>Apprentissage du langage de programmation R</p></li><li><p>Apprentissage de NodeJs et de la librairie ExpressJs</p></li><li><p>Utilisation de docker en prévision de l&#39;implémentation de la solution logiciel au sein du NAS de l&#39;entreprise</p></li><li><p>Consolidation de mes compétences en programmation asynchrone pour la gestion de très gros volumes de données</p></li><li><p>Gestion de projet</p></li></ul></div>',1),R=r({__name:"VisualisationView",setup(g){return(_,v)=>(l(),p("section",null,[d(a,{name:"visualisation"}),m]))}});export{R as default};
