import{_ as e,a as i}from"./Docker-a3b62ffe.js";import{_ as s}from"./JavaScript-d0a12f02.js";import{_ as t,a}from"./cssLogo-9f7e8903.js";import{_ as r}from"./sql-c404065f.js";import{_ as n}from"./ImageSwitch.vue_vue_type_style_index_0_lang-77614d3b.js";import{d as o,o as l,c as p,f as d,a as u}from"./index-0dfb245c.js";import"./sfml-logo-9d8d3c1e.js";import"./arrowleft-1bbbf34d.js";const c=""+new URL("Rlogo-f9a5e1d1.svg",import.meta.url).href,m=""+new URL("vuejs-eb231fe7.png",import.meta.url).href,g=u('<div class="information"><h4>Qu&#39;est-ce que le projet Visualisation ?</h4><div class="textes"><p>Le projet Visualisation est un projet en cours de réalisation dans le cadre de mon alternance au sein de l&#39;entreprise Diag2Tec. Il vise à simplifier la création de graphiques à partir de données saisies par les utilisateurs.</p><p>Ce projet présente de nombreux avantages pour l&#39;entreprise Diag2Tec car il permet d&#39;accéléré grandement leurs processus de création de graphiques nécessaire à la rédaction de comptes rendus à destination de leurs clients.</p><p>En effet, jusqu&#39;a présent, l&#39;entreprise Diag2Tec utilisait le langage de programmation R pour la réalisation de leurs analyses et la création de visuels, ce qui représentait un travail long et fastidieux et dont seul une petite partie des employés y était formé.</p></div><h4>Technologies utilisées :</h4><div class="techno"><img src="'+c+'"><img src="'+e+'"><img src="'+s+'"><img src="'+t+'"><img src="'+a+'"><img src="'+m+'"><img src="'+r+'"><img src="'+i+'"></div><h4>Contraintes techniques :</h4><div class="textes"><p>Ce projet présentes de nombreuses contraintes. Premièrement, les graphiques doivent être générés exclusivement à partir de script R.</p><p>Ensuite, l&#39;application doit pouvoir communiquer avec une base de données pour récupérer les informations nécessaire a la création de ses graphiques.</p><p>De plus, l&#39;utilisateur doit pouvoir facilement ajouté ou mettre à jour des jeux de données.</p><p>Enfin, l&#39;utilisateur doit pouvoir personnaliser le graphique comme il le souhaite (changer le textes, les couleurs, le type de graphique...) et exporter les résultats aux formats pdf, png ou csv.</p></div><h4>Fonctionnalités réalisés :</h4><ul><li><p>Modélisation d&#39;un schéma de base de données évolutif</p></li><li><p>Réalisation d&#39;un système d&#39;importation de données à partir de fichier CSV (création de table dynamique en BDD) permettant d&#39;écraser les contenu existants si besoin.</p></li><li><p>Création d&#39;une API permettant d&#39;interagir avec la base de données</p></li><li><p>Création et intégration de scripts R permettant la réalisation de différentes analyses bio-informatique (analyse d&#39;expression, analyse de survie, analyse différentiel, analyse en composante principale, recherche de mutation)</p></li><li><p>Création d&#39;un outil de personnalisation de graphiques interagissant entre client-serveur / NodeJs-Langage R</p></li></ul><h4>Compétences acquises :</h4><ul><li><p>Acquisition de connaissances en bio-informatique</p></li><li><p>Apprentissage du langage de programmation R</p></li><li><p>Apprentissage de NodeJs et de la librairie ExpressJs</p></li><li><p>Utilisation de docker en prévision de l&#39;implémentation de la solution logiciel au sein du NAS de l&#39;entreprise</p></li><li><p>Utilisation de VueJS pour la réalisation du Front-End</p></li><li><p>Utilisation de web workers</p></li><li><p>Développement dirigé par les tests (TDD)</p></li><li><p>Gestion de projet</p></li></ul></div>',1),D=o({__name:"VisualisationView",setup(_){return(f,h)=>(l(),p("section",null,[d(n,{link:"",name:"visualisation"}),g]))}});export{D as default};
